/*eslint-env mocha*/
/*eslint-disable strict, no-console*/

var assert = require('assert');

describe('basic', function(){
  var nbs = require('../index'), wait = nbs.wait, resume = nbs.resume;

  beforeEach(function(){
    nbs.resetConfig();
  });

  it('should be asynchronous outside, synchronous inside', function(done){
    var outer = 1;
    nbs.run(function(){
      assert.equal(outer, 1);
      setTimeout(function(){
        outer = 2;
        resume();
      }, 0);
      wait();
      assert.equal(outer, 2);

      setTimeout(resume, 10);
      wait();

      setTimeout(resume.bind(null, null, 'a'), 10);
      assert.equal(wait(), 'a');

      assert.equal(wait(setTimeout(resume.bind(null, null, 'b'), 10)), 'b');

      var now = Date.now();
      nbs.sleep(50);
      assert(Date.now()-now>=50);

      done();
    }, done);
    assert.equal(outer, 1);
  });
});