/*eslint-env mocha*/
/*eslint-disable strict, no-console*/

var assert = require('assert');

describe('nested run', function(){
  var nbs = require('../index'), wait = nbs.wait, resume = nbs.resume;

  beforeEach(function(){
    nbs.resetConfig();
  });
  
  it('should be run with the same fiber stack', function(done){
    nbs.run(function(){
      var outer = process.domain.id();
      setTimeout(function(){
        resume(null, 1);
      }, 0);
      var ret = wait(1);
      assert.equal(ret, 1);

      nbs.run(function(){
        var inner = process.domain.id();
        assert.equal(inner, outer);

        setTimeout(function(){
          resume(null, 2);
        });
        ret = wait();
      });
      assert.equal(ret, 2);

      done();
    }, done);
  });

});