/*eslint-env mocha*/
/*eslint-disable strict, no-console*/

var assert = require('assert');

describe('non-fiber domain', function(){
  
  it('shoud throw on a non-fiber-stack domain', function(){
    var domain = require('domain').create();
    assert.throws(function(){
      domain.wait();
    });
    
    assert.throws(function(){
      domain.resume();
    });
  });

  
});