/*eslint-env mocha*/
/*eslint-disable strict, no-console*/

var assert = require('assert');

describe('sync and async callback', function(){
  var nbs = require('../index'), wait = nbs.wait, resume = nbs.resume;

  beforeEach(function(){
    nbs.resetConfig();
  });
  
  it('should run the sync and async callbacks one-by-one', function(done){
    function syncCallbackFunc(cb){
      cb(null, 1, new Error('async function error'));
    }
    nbs.run(function(){
      var pair = nbs.pair();
      syncCallbackFunc(function(){
        pair.resume.apply(pair, arguments);
      });
      var ret = pair.wait();
      assert.equal(ret[0], 1);

      setTimeout(function(){
        resume(null, 'b', 2, null);
      }, 0);
      var ret2 = wait();
      assert.equal(ret2[0], 'b');
      assert.equal(ret2[1], 2);
      
      //another sync callback
      pair = nbs.pair();
      (function(){
        pair.resume(null, 'c', 3, null);
      })();
      var ret3 = pair.wait();
      assert.equal(ret3[0], 'c');
      assert.equal(ret3[1], 3);

      done();
    }, done);
  });

});